    // ----- BALANCE -----

    function levelSnrBalance() public view returns (uint256 _stakedBalance) {
        return _levelBalance(0);
    }

    function levelMzeBalance() public view returns (uint256 _stakedBalance) {
        return _levelBalance(1);
    }

