// SPDX-License-Identifier: BUSL-1.1
// Last deployed from commit: 6d86e26ff5a23ee2fa4ddb079d0569c41d45f7dd;
pragma solidity 0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@redstone-finance/evm-connector/contracts/data-services/AvalancheDataServiceConsumerBase.sol";

import "../../ReentrancyGuardKeccak.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import {DiamondStorageLib} from "../../lib/DiamondStorageLib.sol";
import "../../OnlyOwnerOrInsolvent.sol";
import "../../interfaces/IWrappedNativeToken.sol";

//This path is updated during deployment
import "../../lib/local/DeploymentConstants.sol";

contract CaiFacet is
    ReentrancyGuardKeccak,
    OnlyOwnerOrInsolvent,
    AvalancheDataServiceConsumerBase
{
    using TransferHelper for address;

    address private constant INDEX_ROUTER =
        0xD6dd95610fC3A3579a2C32fe06158d8bfB8F4eE9;

    address private constant CAI = 0x48f88A3fE843ccb0b5003e70B4192c1d7448bEf0;

    function mintCai(
        bytes4 selector,
        bytes memory data,
        address fromToken,
        uint256 fromAmount,
        uint256 maxSlippage
    ) external nonReentrant onlyOwner remainsSolvent {
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();

        require(
            fromAmount > 0,
            "Amount of tokens to sell has to be greater than 0"
        );

        bytes32 fromTokenSymbol = tokenManager.tokenAddressToSymbol(fromToken);

        address(fromToken).safeApprove(INDEX_ROUTER, 0);
        address(fromToken).safeApprove(INDEX_ROUTER, fromAmount);

        uint256 beforeCaiBalance = IERC20(CAI).balanceOf(address(this));
        uint256 beforeFromTokenBalance = IERC20(fromToken).balanceOf(address(this));

        (bool success, ) = INDEX_ROUTER.call((abi.encodePacked(selector, data)));
        require(success, "Mint failed");

        uint256 afterCaiBalance = IERC20(CAI).balanceOf(address(this));
        uint256 afterFromTokenBalance = IERC20(fromToken).balanceOf(address(this));

        uint256 fromTokenUsed = beforeFromTokenBalance - afterFromTokenBalance;
        uint256 caiBoughtAmount = afterCaiBalance - beforeCaiBalance;

        CAI.safeApprove(INDEX_ROUTER, 0);
        CAI.safeApprove(INDEX_ROUTER, type(uint256).max);

        checkSlippage(fromTokenSymbol, fromTokenUsed, "CAI", caiBoughtAmount, maxSlippage);

        _increaseExposure(tokenManager, CAI, caiBoughtAmount);
        _decreaseExposure(tokenManager, fromToken, fromTokenUsed);

        emit CaiMinted(
            msg.sender,
            fromTokenSymbol,
            fromAmount,
            caiBoughtAmount,
            block.timestamp
        );
    }

    function burnCai(
        bytes4 selector,
        bytes memory data,
        uint256 shares,
        address toToken,
        uint256 maxSlippage
    ) external nonReentrant onlyOwnerOrInsolvent remainsSolvent {
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();

        require(shares > 0, "Amount of tokens to sell has to be greater than 0");

        bytes32 toTokenSymbol = tokenManager.tokenAddressToSymbol(toToken);

        uint256 caiBurnt;
        uint256 boughtAmount;

        {
            uint256 beforeCaiBalance = IERC20(CAI).balanceOf(address(this));
            uint256 beforeTokenBalance = IERC20(toToken).balanceOf(address(this));

            (bool success, ) = INDEX_ROUTER.call((abi.encodePacked(selector, data)));
            require(success, "Burn failed");

            uint256 afterCaiBalance = IERC20(CAI).balanceOf(address(this));
            uint256 afterTokenBalance = IERC20(toToken).balanceOf(address(this));

            caiBurnt = beforeCaiBalance - afterCaiBalance;
            boughtAmount = afterTokenBalance - beforeTokenBalance;
        }

        checkSlippage("CAI", caiBurnt, toTokenSymbol, boughtAmount, maxSlippage);

        _decreaseExposure(tokenManager, CAI, caiBurnt);
        _increaseExposure(tokenManager, toToken, boughtAmount);

        emit CaiBurned(
            msg.sender,
            shares,
            toTokenSymbol,
            boughtAmount,
            block.timestamp
        );
    }

    function checkSlippage(
        bytes32 inputAsset,
        uint256 inputAmount,
        bytes32 outputAsset,
        uint256 outputAmount,
        uint256 maxSlippage
    ) internal view {        
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();

        bytes32[] memory assets = new bytes32[](2);
        assets[0] = inputAsset;
        assets[1] = outputAsset;

        uint256[] memory prices = getOracleNumericValuesFromTxMsg(assets);

        IERC20Metadata inputToken = IERC20Metadata(tokenManager.getAssetAddress(inputAsset, true));
        IERC20Metadata outputToken = IERC20Metadata(tokenManager.getAssetAddress(outputAsset, true));

        uint256 inputValue = prices[0] * inputAmount * 10 ** 10 / 10 ** inputToken.decimals();
        uint256 outputValue = prices[1] * outputAmount * 10 ** 10 / 10 ** outputToken.decimals();
        uint256 diff = inputValue > outputValue
            ? (inputValue - outputValue)
            : (outputValue - inputValue);
        uint256 slippage = (diff * 10000) / inputValue;

        require(slippage <= maxSlippage, "Slippage too high");
    }

    modifier onlyOwner() {
        DiamondStorageLib.enforceIsContractOwner();
        _;
    }

    /* ========== RECEIVE AVAX FUNCTION ========== */
    receive() external payable {}

    /**
     * @dev emitted after a CAI mint
     * @param user the address of user minting the CAI
     * @param soldAsset sold by the user
     * @param amountSold amount of tokens sold
     * @param amountMinted amount of CAI minted
     * @param timestamp time of the mint
     **/
    event CaiMinted(
        address indexed user,
        bytes32 indexed soldAsset,
        uint256 amountSold,
        uint256 amountMinted,
        uint256 timestamp
    );

    /**
     * @dev emitted after a CAI mint
     * @param user the address of user minting the CAI
     * @param shares amount of CAI burned
     * @param buyAsset redeemed by the user
     * @param amountRedeemed amount of CAI minted
     * @param timestamp time of the mint
     **/
    event CaiBurned(
        address indexed user,
        uint256 indexed shares,
        bytes32 indexed buyAsset,
        uint256 amountRedeemed,
        uint256 timestamp
    );
}

contracts/facets/avalanche/CurveFacet.so.content:
// SPDX-License-Identifier: BUSL-1.1
// Last deployed from commit: 799a1765b64edc5c158198ef84f785af79e234ae;
pragma solidity 0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

import "../../ReentrancyGuardKeccak.sol";
import "../../OnlyOwnerOrInsolvent.sol";
import "../../interfaces/ITokenManager.sol";
import "../../interfaces/ICurvePool.sol";
import {DiamondStorageLib} from "../../lib/DiamondStorageLib.sol";

//This path is updated during deployment
import "../../lib/local/DeploymentConstants.sol";

contract CurveFacet is ReentrancyGuardKeccak, OnlyOwnerOrInsolvent {
    using TransferHelper for address;

    // Used to deposit/withdraw tokens
    address private constant CURVE_POOL_ADDRESS = 0x58e57cA18B7A47112b877E31929798Cd3D703b0f;
    // crvUSDBTCETH
    bytes32 private constant CURVE_TOKEN_SYMBOL = "crvUSDBTCETH";

    /**
     * Stakes tokens in Curve atricrypto pool
     * @param amounts amounts of tokens to be staked
     **/
    function stakeCurve(uint256[5] memory amounts) external nonReentrant onlyOwner recalculateAssetsExposure remainsSolvent {
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();

        address curveTokenAddress = DeploymentConstants.getTokenManager().getAssetAddress(CURVE_TOKEN_SYMBOL, false);
        IERC20 curveToken = IERC20(curveTokenAddress);
        uint256 initialCurveBalance = curveToken.balanceOf(address(this));

        bool allZero = true;
        uint256 numStakeTokens;
        for (uint256 i; i < 5; ++i ) {
            IERC20 token = IERC20(tokenManager.getAssetAddress(getTokenSymbol(i), false));
            amounts[i] = Math.min(token.balanceOf(address(this)), amounts[i]);
            if (amounts[i] > 0) {
                allZero = false;
                address(token).safeApprove(CURVE_POOL_ADDRESS, 0);
                address(token).safeApprove(CURVE_POOL_ADDRESS, amounts[i]);
                ++numStakeTokens;
            }
        }
        require(!allZero, "Cannot stake 0 tokens");

        bytes32[] memory stakedAssets = new bytes32[](numStakeTokens);
        uint256[] memory stakedAmounts = new uint256[](numStakeTokens);
        uint256 idx;
        for (uint256 i; i < 5; ++i ) {
            if (amounts[i] > 0) {
                stakedAssets[idx] = getTokenSymbol(i);
                stakedAmounts[idx++] = amounts[i];
            }
        }

        ICurvePool(CURVE_POOL_ADDRESS).add_liquidity(amounts, 0);

        // Add/remove owned tokens
        DiamondStorageLib.addOwnedAsset(CURVE_TOKEN_SYMBOL, curveTokenAddress);
        for (uint256 i; i < 5; ++i ) {
            IERC20 token = IERC20(tokenManager.getAssetAddress(getTokenSymbol(i), false));
            if (amounts[i] > 0 && token.balanceOf(address(this)) == 0) {
                DiamondStorageLib.removeOwnedAsset(getTokenSymbol(i));
            }
        }

        emit Staked(
            msg.sender,
            stakedAssets,
            curveTokenAddress,
            stakedAmounts,
            curveToken.balanceOf(address(this)) - initialCurveBalance,
            block.timestamp
        );
    }

    /**
     * Unstakes tokens from Curve atricrypto pool
     * @param amount amount of token to be unstaked
     **/
    function unstakeCurve(uint256 amount, uint256[5] memory min_amounts) external nonReentrant onlyOwnerOrInsolvent recalculateAssetsExposure {
        ICurvePool pool = ICurvePool(CURVE_POOL_ADDRESS);
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();
        address curveTokenAddress = tokenManager.getAssetAddress(CURVE_TOKEN_SYMBOL, true);
        IERC20 curveToken = IERC20(curveTokenAddress);
        uint256 curveTokenBalance = curveToken.balanceOf(address(this));
        uint256[5] memory initialDepositTokenBalances;
        for (uint256 i; i < 5; ++i) {
            IERC20 depositToken = IERC20(tokenManager.getAssetAddress(getTokenSymbol(i), true));
            initialDepositTokenBalances[i] = depositToken.balanceOf(address(this));
        }
        amount = Math.min(curveTokenBalance, amount);

        curveTokenAddress.safeApprove(CURVE_POOL_ADDRESS, 0);
        curveTokenAddress.safeApprove(CURVE_POOL_ADDRESS, amount);
        pool.remove_liquidity(amount, min_amounts);

        // Add/remove owned tokens
        uint256[5] memory unstakedAmounts;
        for (uint256 i; i < 5; ++i) {
            IERC20 depositToken = IERC20(tokenManager.getAssetAddress(getTokenSymbol(i), true));
            unstakedAmounts[i] = depositToken.balanceOf(address(this)) - initialDepositTokenBalances[i];
            DiamondStorageLib.addOwnedAsset(getTokenSymbol(i), address(depositToken));
        }
        uint256 newCurveTokenBalance = curveToken.balanceOf(address(this));
        if(newCurveTokenBalance == 0) {
            DiamondStorageLib.removeOwnedAsset(CURVE_TOKEN_SYMBOL);
        }

        emit Unstaked(
            msg.sender,
            getTokenSymbols(),
            curveTokenAddress,
            unstakedAmounts,
            curveTokenBalance - newCurveTokenBalance,
            block.timestamp
        );
    }

    // TODO: Add slippage control
    /**
     * Unstakes one token from Curve atricrypto pool
     * @param i index of token to be unstaked
     * @param amount amount of token to be unstaked
     **/
    function unstakeOneTokenCurve(uint256 i, uint256 amount) external nonReentrant onlyOwnerOrInsolvent recalculateAssetsExposure {
        require(i < 5, "Invalid token index");
        ICurvePool pool = ICurvePool(CURVE_POOL_ADDRESS);
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();
        address curveTokenAddress = tokenManager.getAssetAddress(CURVE_TOKEN_SYMBOL, true);
        IERC20 curveToken = IERC20(curveTokenAddress);
        IERC20 depositToken = IERC20(tokenManager.getAssetAddress(getTokenSymbol(i), true));
        uint256 initialDepositTokenBalance = depositToken.balanceOf(address(this));
        uint256 curveTokenBalance = curveToken.balanceOf(address(this));
        uint256 maxWithdrawAmount = pool.calc_withdraw_one_coin(curveTokenBalance, i);
        amount = Math.min(maxWithdrawAmount, amount);

        uint256 burnAmount = curveTokenBalance * amount / maxWithdrawAmount;
        curveTokenAddress.safeApprove(CURVE_POOL_ADDRESS, 0);
        curveTokenAddress.safeApprove(CURVE_POOL_ADDRESS, burnAmount);
        pool.remove_liquidity_one_coin(burnAmount, i, 0);

        // Add/remove owned tokens
        DiamondStorageLib.addOwnedAsset(getTokenSymbol(i), address(depositToken));
        uint256 newCurveTokenBalance = curveToken.balanceOf(address(this));
        if(newCurveTokenBalance == 0) {
            DiamondStorageLib.removeOwnedAsset(CURVE_TOKEN_SYMBOL);
        }

        emit UnstakedOneToken(
            msg.sender,
            getTokenSymbol(i),
            curveTokenAddress,
            depositToken.balanceOf(address(this)) - initialDepositTokenBalance,
            curveTokenBalance - newCurveTokenBalance,
            block.timestamp
        );
    }

    // INTERNAL FUNCTIONS

    function getTokenSymbols() internal pure returns (bytes32[5] memory tokenSymbols) {
        tokenSymbols = [
            bytes32("DAIe"),
            bytes32("USDCe"),
            bytes32("USDT.e"),
            bytes32("WBTCe"),
            bytes32("ETH")
        ];
    }

    function getTokenSymbol(uint256 i) internal pure returns (bytes32) {
        return getTokenSymbols()[i];
    }

    // MODIFIERS

    modifier onlyOwner() {
        DiamondStorageLib.enforceIsContractOwner();
        _;
    }

    // EVENTS

    /**
        * @dev emitted when user stakes assets
        * @param user the address executing staking
        * @param assets the assets that were staked
        * @param vault address of the vault token
        * @param depositTokenAmounts how much of deposit tokens was staked
        * @param receiptTokenAmount how much of receipt token was received
        * @param timestamp of staking
    **/
    event Staked(address indexed user, bytes32[] assets, address indexed vault, uint256[] depositTokenAmounts, uint256 receiptTokenAmount, uint256 timestamp);

    /**
        * @dev emitted when user unstakes assets
        * @param user the address executing staking
        * @param assets the assets that were unstaked
        * @param vault address of the vault token
        * @param depositTokenAmounts how much of deposit tokens was received
        * @param receiptTokenAmount how much of receipt token was unstaked
        * @param timestamp of unstaking
    **/
    event Unstaked(address indexed user, bytes32[5] assets, address indexed vault, uint256[5] depositTokenAmounts, uint256 receiptTokenAmount, uint256 timestamp);

    /**
        * @dev emitted when user unstakes an asset
        * @param user the address executing unstaking
        * @param vault address of the vault token
        * @param asset the asset that was unstaked
        * @param depositTokenAmount how much deposit token was received
        * @param receiptTokenAmount how much receipt token was unstaked
        * @param timestamp of unstaking
    **/
    event UnstakedOneToken(address indexed user, bytes32 indexed asset, address indexed vault, uint256 depositTokenAmount, uint256 receiptTokenAmount, uint256 timestamp);
}

contracts/facets/avalanche/GLPFacet.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// Last deployed from commit: 499a35c62f8a913d89f7faf78bf5c6b3cea2ee8b;
pragma solidity 0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "../../interfaces/facets/avalanche/IGLPRewarder.sol";
import "../../interfaces/facets/avalanche/IRewardRouterV2.sol";
import "../../interfaces/facets/avalanche/IRewardTracker.sol";
import "../../ReentrancyGuardKeccak.sol";
import {DiamondStorageLib} from "../../lib/DiamondStorageLib.sol";
import "../../OnlyOwnerOrInsolvent.sol";
import "../../interfaces/ITokenManager.sol";

//This path is updated during deployment
import "../../lib/local/DeploymentConstants.sol";

contract GLPFacet is ReentrancyGuardKeccak, OnlyOwnerOrInsolvent {
    using TransferHelper for address;

    // Used to claim GLP fees
    address private constant REWARD_ROUTER_ADDRESS = 0xa192D0681E2b9484d1fA48083D36B8A2D0Da1809;
    // Used to mint/redeem GLP
    address private constant GLP_REWARD_ROUTER_ADDRESS = 0xB70B91CE0771d3f4c81D87660f71Da31d48eB3B3;
    // Used to approve tokens to mint GLP with
    address private constant GLP_MANAGER_ADDRESS = 0xD152c7F25db7F4B95b7658323c5F33d176818EE4;
    // fsGLP
    address private constant GLP_TOKEN_ADDRESS = 0x9e295B5B976a184B14aD8cd72413aD846C299660;

    function claimGLpFees() external nonReentrant onlyOwner noBorrowInTheSameBlock remainsSolvent {
        IRewardRouterV2 rewardRouter = IRewardRouterV2(REWARD_ROUTER_ADDRESS);
        IRewardTracker rewardTracker = IRewardTracker(rewardRouter.feeGlpTracker());
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();

        require(rewardTracker.claimable(address(this)) > 0, "There are no claimable fees");

        IERC20Metadata wavaxToken = getERC20TokenInstance("AVAX", false);
        uint256 initialWavaxBalance = wavaxToken.balanceOf(address(this));

        rewardRouter.claimFees();

        uint256 postClaimingWavaxBalance = wavaxToken.balanceOf(address(this));
        uint256 wavaxClaimed = postClaimingWavaxBalance-initialWavaxBalance;
        _increaseExposure(tokenManager, address(wavaxToken), wavaxClaimed);

        emit GLPFeesClaim(msg.sender, wavaxClaimed, block.timestamp);
    }

    function mintAndStakeGlp(address _token, uint256 _amount, uint256 _minUsdg, uint256 _minGlp) external nonReentrant onlyOwner noBorrowInTheSameBlock remainsSolvent{
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();
        require(tokenManager.isTokenAssetActive(GLP_TOKEN_ADDRESS), "GLP not supported.");
        require(tokenManager.isTokenAssetActive(_token), "Asset not supported.");

        IERC20Metadata tokenToMintWith = IERC20Metadata(_token);
        bytes32 tokenToMintWithSymbol = tokenManager.tokenAddressToSymbol(_token);
        IGLPRewarder glpRewarder = IGLPRewarder(GLP_REWARD_ROUTER_ADDRESS);
        IERC20Metadata glpToken = IERC20Metadata(GLP_TOKEN_ADDRESS);

        uint256 glpInitialBalance = glpToken.balanceOf(address(this));

        _amount = Math.min(tokenToMintWith.balanceOf(address(this)), _amount);

        require(_amount > 0, "Amount of GLP to mint  has to be greater than 0");

        _token.safeApprove(GLP_MANAGER_ADDRESS, 0);
        _token.safeApprove(GLP_MANAGER_ADDRESS, _amount);

        uint256 glpOutputAmount = glpRewarder.mintAndStakeGlp(_token, _amount, _minUsdg, _minGlp);

        require((glpToken.balanceOf(address(this)) - glpInitialBalance) == glpOutputAmount, "GLP minted and balance difference mismatch");
        require(glpOutputAmount >=_minGlp, "Insufficient output amount");

        _increaseExposure(tokenManager, GLP_TOKEN_ADDRESS, glpOutputAmount);
        _decreaseExposure(tokenManager, _token, _amount);

        emit GLPMint(
            msg.sender,
            tokenToMintWithSymbol,
            _amount,
            glpOutputAmount,
            block.timestamp
        );

    }

    function unstakeAndRedeemGlp(address _tokenOut, uint256 _glpAmount, uint256 _minOut) external nonReentrant onlyOwnerOrInsolvent noBorrowInTheSameBlock    {
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();
        require(tokenManager.isTokenAssetActive(_tokenOut), "Asset not supported.");

        IERC20Metadata redeemedToken = IERC20Metadata(_tokenOut);
        bytes32 redeemedTokenSymbol = tokenManager.tokenAddressToSymbol(_tokenOut);
        IGLPRewarder glpRewarder = IGLPRewarder(GLP_REWARD_ROUTER_ADDRESS);
        IERC20Metadata glpToken = IERC20Metadata(GLP_TOKEN_ADDRESS);

        uint256 redeemedTokenInitialBalance = redeemedToken.balanceOf(address(this));
        _glpAmount = Math.min(glpToken.balanceOf(address(this)), _glpAmount);

        require(_glpAmount > 0, "Amount of GLP to redeem has to be greater than 0");

        uint256 redeemedAmount = glpRewarder.unstakeAndRedeemGlp(_tokenOut, _glpAmount, _minOut, address(this));

        require((redeemedToken.balanceOf(address(this)) - redeemedTokenInitialBalance) == redeemedAmount, "Redeemed token amount and balance difference mismatch");
        require(redeemedAmount >= _minOut, "Insufficient output amount");

        _decreaseExposure(tokenManager, GLP_TOKEN_ADDRESS, _glpAmount);
        _increaseExposure(tokenManager, _tokenOut, redeemedAmount);

        emit GLPRedemption(
            msg.sender,
            redeemedTokenSymbol,
            _glpAmount,
            redeemedAmount,
            block.timestamp
        );

    }

    modifier onlyOwner() {
        DiamondStorageLib.enforceIsContractOwner();
        _;
    }

    /**
     * @dev emitted after a GLP token mint
     * @param user the address of user minting GLP
     * @param tokenToMintWith token which GLP was minted with
     * @param tokenToMintWithAmount amount of token used to mint GLP
     * @param glpOutputAmount amount of GLP minted
     * @param timestamp time of the mint
     **/
    event GLPMint(address indexed user, bytes32 indexed tokenToMintWith, uint256 tokenToMintWithAmount, uint256 glpOutputAmount, uint256 timestamp);

    /**
  * @dev emitted after a GLP token redemption
  * @param user the address of user redeeming GLP
  * @param redeemedToken token which GLP was redeemed into
  * @param glpRedeemedAmount amount of GLP redeemed
  * @param redeemedTokenAmount amount of redeemedToken redeemed
  * @param timestamp time of the redemption
  **/
    event GLPRedemption(address indexed user, bytes32 indexed redeemedToken, uint256 glpRedeemedAmount, uint256 redeemedTokenAmount, uint256 timestamp);

    /**
    * @dev emitted after claiming GLP fees
    * @param user the address of user claiming fees
    * @param wavaxAmountClaimed amount of wavax fees that were claimed
    * @param timestamp time of claiming the fees
    **/
    event GLPFeesClaim(address indexed user, uint256 wavaxAmountClaimed, uint256 timestamp);
}

contracts/facets/avalanche/GMDFacet.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

import "../../ReentrancyGuardKeccak.sol";
import "../../OnlyOwnerOrInsolvent.sol";
import {DiamondStorageLib} from "../../lib/DiamondStorageLib.sol";
import "../../interfaces/facets/avalanche/IGMDFacet.sol";
import "../../interfaces/facets/IGMDVault.sol";
//This path is updated during deployment
import "../../lib/local/DeploymentConstants.sol";

contract GMDFacet is ReentrancyGuardKeccak, OnlyOwnerOrInsolvent{
    using TransferHelper for address;

    // CONSTANTS

    address private constant GMDVaultAddress = 0x5517c5F22177BcF7b320A2A5daF2334344eFb38C;

    // PUBLIC FUNCTIONS

    function gmdStakeUSDC(uint256 amountStaked, uint256 minSharesOut) public {
        IGMDFacet.StakingDetails memory position = IGMDFacet.StakingDetails({
            asset : 0xB97EF9Ef8734C71904D8002F8b6Bc66Dd9c48a6E,
            receiptToken: 0x33f0a866d9024d44de2E0602f4C9B94755944B6F,
            symbol : "USDC",
            identifier : "gmdUSDC",
            amountStaked: amountStaked,
            minSharesOut: minSharesOut,
            pid: 0
        });
        gmdStakeToken(position);
    }

    function gmdStakeAVAX(uint256 amountStaked, uint256 minSharesOut) public {
        IGMDFacet.StakingDetails memory position = IGMDFacet.StakingDetails({
            asset : 0xB31f66AA3C1e785363F0875A1B74E27b85FD66c7,
            receiptToken: 0x13AF25f924056d4D4668705C33aB9b70D505050e,
            symbol : "AVAX",
            identifier : "gmdWAVAX",
            amountStaked: amountStaked,
            minSharesOut: minSharesOut,
            pid: 1
        });
        gmdStakeToken(position);
    }

    function gmdStakeBTCb(uint256 amountStaked, uint256 minSharesOut) public {
        IGMDFacet.StakingDetails memory position = IGMDFacet.StakingDetails({
            asset : 0x152b9d0FdC40C096757F570A51E494bd4b943E50,
            receiptToken: 0x8fe3024351B9a51a3439183e940c2aF3994DD52F,
            symbol : "BTC",
            identifier : "gmdBTCb",
            amountStaked: amountStaked,
            minSharesOut: minSharesOut,
            pid: 2
        });
        gmdStakeToken(position);
    }

    function gmdStakeWETHe(uint256 amountStaked, uint256 minSharesOut) public {
        IGMDFacet.StakingDetails memory position = IGMDFacet.StakingDetails({
            asset : 0x49D5c2BdFfac6CE2BFdB6640F4F80f226bc10bAB,
            receiptToken: 0xE28c95e9EB0f6D16b05D265cAa4BcEE9E5C2e625,
            symbol : "ETH",
            identifier : "gmdWETHe",
            amountStaked: amountStaked,
            minSharesOut: minSharesOut,
            pid: 3
        });
        gmdStakeToken(position);
    }

    // PUBLIC FUNCTIONS

    function gmdUnstakeUSDC(uint256 amountUnstaked, uint256 minTokenOut) public {
        IGMDFacet.UnstakingDetails memory position = IGMDFacet.UnstakingDetails({
            asset : 0xB97EF9Ef8734C71904D8002F8b6Bc66Dd9c48a6E,
            receiptToken: 0x33f0a866d9024d44de2E0602f4C9B94755944B6F,
            symbol : "USDC",
            identifier : "gmdUSDC",
            amountUnstaked: amountUnstaked,
            minTokenOut: minTokenOut,
            pid: 0
        });
        gmdUnstakeToken(position);
    }

    function gmdUnstakeAVAX(uint256 amountUnstaked, uint256 minTokenOut) public {
        IGMDFacet.UnstakingDetails memory position = IGMDFacet.UnstakingDetails({
            asset : 0xB31f66AA3C1e785363F0875A1B74E27b85FD66c7,
            receiptToken: 0x13AF25f924056d4D4668705C33aB9b70D505050e,
            symbol : "AVAX",
            identifier : "gmdWAVAX",
            amountUnstaked: amountUnstaked,
            minTokenOut: minTokenOut,
            pid: 1
            });
        gmdUnstakeToken(position);
    }

    function gmdUnstakeBTCb(uint256 amountUnstaked, uint256 minTokenOut) public {
        IGMDFacet.UnstakingDetails memory position = IGMDFacet.UnstakingDetails({
            asset : 0x152b9d0FdC40C096757F570A51E494bd4b943E50,
            receiptToken: 0x8fe3024351B9a51a3439183e940c2aF3994DD52F,
            symbol : "BTC",
            identifier : "gmdBTCb",
            amountUnstaked: amountUnstaked,
            minTokenOut: minTokenOut,
            pid: 2
        });
        gmdUnstakeToken(position);
    }

    function gmdUnstakeWETHe(uint256 amountUnstaked, uint256 minTokenOut) public {
        IGMDFacet.UnstakingDetails memory position = IGMDFacet.UnstakingDetails({
        asset : 0x49D5c2BdFfac6CE2BFdB6640F4F80f226bc10bAB,
        receiptToken: 0xE28c95e9EB0f6D16b05D265cAa4BcEE9E5C2e625,
        symbol : "ETH",
        identifier : "gmdWETHe",
        amountUnstaked: amountUnstaked,
        minTokenOut: minTokenOut,
        pid: 3
        });
        gmdUnstakeToken(position);
    }

    // INTERNAL FUNCTIONS
    /**
    * Stakes token in GMD
    * @dev This function uses the redstone-evm-connector
    **/
    function gmdStakeToken(IGMDFacet.StakingDetails memory position) internal
    onlyOwner nonReentrant recalculateAssetsExposure remainsSolvent {
        IGMDVault vault = IGMDVault(GMDVaultAddress);
        IERC20Metadata stakedToken = getERC20TokenInstance(position.symbol, false);
        IERC20Metadata receiptToken = IERC20Metadata(position.receiptToken);
        uint256 initialReceiptTokenBalance = receiptToken.balanceOf(address(this));

        uint256 amountStaked = Math.min(stakedToken.balanceOf(address(this)), position.amountStaked);
        require(amountStaked > 0, "Cannot stake 0 tokens");

        address(stakedToken).safeApprove(GMDVaultAddress, 0);
        address(stakedToken).safeApprove(GMDVaultAddress, amountStaked);

        vault.enter(amountStaked, position.pid);

        uint256 sharesReceived = receiptToken.balanceOf(address(this)) - initialReceiptTokenBalance;
        require(sharesReceived >= position.minSharesOut, "Insufficient shares output");

        DiamondStorageLib.addOwnedAsset(position.identifier, position.receiptToken);
        if (stakedToken.balanceOf(address(this)) == 0) {
            DiamondStorageLib.removeOwnedAsset(position.symbol);
        }

        emit Staked(
            msg.sender,
            position.symbol,
            address(position.receiptToken),
            amountStaked,
            sharesReceived,
            block.timestamp
        );
    }

    /**
    * Unstakes token from GMD
    * IMPORTANT: This method can be used by anyone when a loan is insolvent. This operation can be gas-costly, that is why
    * it may be necessary to perform it in a separate transaction to liquidation
    * @dev This function uses the redstone-evm-connector
    **/
    function gmdUnstakeToken(IGMDFacet.UnstakingDetails memory position) internal
    onlyOwnerOrInsolvent recalculateAssetsExposure nonReentrant {
        IGMDVault vault = IGMDVault(GMDVaultAddress);
        IERC20Metadata unstakedToken = getERC20TokenInstance(position.symbol, false);
        IERC20Metadata receiptToken = IERC20Metadata(position.receiptToken);

        uint256 amountUnstaked = Math.min(receiptToken.balanceOf(address(this)), position.amountUnstaked);
        require(amountUnstaked > 0, "Cannot unstake 0 tokens");

        uint256 initialUnstakedTokenBalance = unstakedToken.balanceOf(address(this));

        vault.leave(amountUnstaked, position.pid);

        uint256 receivedUnstakedToken = unstakedToken.balanceOf(address(this)) - initialUnstakedTokenBalance;
        require(receivedUnstakedToken >= position.minTokenOut, "Insufficient staked token output");

        if (receiptToken.balanceOf(address(this)) == 0) {
            DiamondStorageLib.removeOwnedAsset(position.identifier);
        }
        DiamondStorageLib.addOwnedAsset(position.symbol, address(unstakedToken));

        emit Unstaked(
            msg.sender,
            position.symbol,
            address(position.receiptToken),
            receivedUnstakedToken,
            amountUnstaked,
            block.timestamp
        );
    }

    modifier onlyOwner() {
        DiamondStorageLib.enforceIsContractOwner();
        _;
    }

    // EVENTS

    /**
        * @dev emitted when user stakes an asset
        * @param user the address executing staking
        * @param asset the asset that was staked
        * @param vault address of receipt token
        * @param depositTokenAmount how much of deposit token was staked
        * @param receiptTokenAmount how much of receipt token was received
        * @param timestamp of staking
    **/
    event Staked(address indexed user, bytes32 indexed asset, address indexed vault, uint256 depositTokenAmount, uint256 receiptTokenAmount, uint256 timestamp);

    /**
        * @dev emitted when user unstakes an asset
        * @param user the address executing unstaking
        * @param asset the asset that was unstaked
        * @param vault address of receipt token
        * @param depositTokenAmount how much deposit token was unstaked
        * @param receiptTokenAmount how much receipt token was redeemed
        * @param timestamp of unstaking
    **/
    event Unstaked(address indexed user, bytes32 indexed asset, address indexed vault, uint256 depositTokenAmount, uint256 receiptTokenAmount, uint256 timestamp);
}

