        emit BeaconUpgraded(newBeacon);
        if (data.length > 0 || forceCall) {
            // 0xe1c7392a = init()
            Address.functionDelegateCall(IDiamondBeacon(newBeacon).implementation(0xe1c7392a), data);
        }
    }
}

