// SPDX-License-Identifier: BUSL-1.1
// Last deployed from commit: ;
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "../../ReentrancyGuardKeccak.sol";
import "@uniswap/lib/contracts/libraries/TransferHelper.sol";
import "../../OnlyOwnerOrInsolvent.sol";
import "../../interfaces/facets/IBeefyFinance.sol";

import {DiamondStorageLib} from "../../lib/DiamondStorageLib.sol";

//This path is updated during deployment
import "../../lib/avalanche/DeploymentConstants.sol";

contract BeefyFinanceAvalancheFacet is ReentrancyGuardKeccak, OnlyOwnerOrInsolvent {
    using TransferHelper for address payable;
    using TransferHelper for address;

    // Vaults
    address private constant MOO_PNG_AVAX_USDC_LP = 0xf3340EdF16563D52C7E7C576F2fCC8f3D52464aB;
    address private constant MOO_PNG_AVAX_USDCe_LP = 0x9B02209a331c072637C6eBd34cdCD6b6A16987a9;
    address private constant MOO_TJ_AVAX_USDC_LP = 0x7E5bC7088aB3Da3e7fa1Aa7ceF1dC73F5B00681c;

    // LPs
    address private constant PNG_AVAX_USDC_LP = 0x0e0100Ab771E9288e0Aa97e11557E6654C3a9665;
    address private constant PNG_AVAX_USDCe_LP = 0xbd918Ed441767fe7924e99F6a0E0B568ac1970D9;
    address private constant TJ_AVAX_USDC_LP = 0xf4003F4efBE8691B60249E6afbD307aBE7758adb;

    // ----- STAKE -----

    /**
      * Stakes PNG_AVAX_USDC_LP in the Beefy protocol
      * @dev This function uses the redstone-evm-connector
      * @param amount amount of PNG_AVAX_USDC_LP to be staked
    **/
    function stakePngUsdcAvaxLpBeefy(uint256 amount) public onlyOwnerOrInsolvent nonReentrant recalculateAssetsExposure remainsSolvent {
        _stakeLpBeefy(IBeefyFinance.BeefyStakingDetails({
            lpTokenAddress: PNG_AVAX_USDC_LP,
            vaultAddress: MOO_PNG_AVAX_USDC_LP,
            lpTokenSymbol: "PNG_AVAX_USDC_LP",
            vaultTokenSymbol: "MOO_PNG_AVAX_USDC_LP",
            amount: amount
        }));
    }

    /**
      * Stakes PNG_AVAX_USDCe_LP in the Beefy protocol
      * @dev This function uses the redstone-evm-connector
      * @param amount amount of PNG_AVAX_USDCe_LP to be staked
    **/
    function stakePngUsdceAvaxLpBeefy(uint256 amount) public onlyOwnerOrInsolvent nonReentrant recalculateAssetsExposure remainsSolvent {
        _stakeLpBeefy(IBeefyFinance.BeefyStakingDetails({
        lpTokenAddress: PNG_AVAX_USDCe_LP,
        vaultAddress: MOO_PNG_AVAX_USDCe_LP,
        lpTokenSymbol: "PNG_AVAX_USDCe_LP",
        vaultTokenSymbol: "MOO_PNG_AVAX_USDCe_LP",
        amount: amount
        }));
    }

    /**
      * Stakes TJ_AVAX_USDC_LP in the Beefy protocol
      * @dev This function uses the redstone-evm-connector
      * @param amount amount of TJ_AVAX_USDC_LP to be staked
    **/
    function stakeTjUsdcAvaxLpBeefy(uint256 amount) public onlyOwnerOrInsolvent nonReentrant recalculateAssetsExposure remainsSolvent {
        _stakeLpBeefy(IBeefyFinance.BeefyStakingDetails({
        lpTokenAddress: TJ_AVAX_USDC_LP,
        vaultAddress: MOO_TJ_AVAX_USDC_LP,
        lpTokenSymbol: "TJ_AVAX_USDC_LP",
        vaultTokenSymbol: "MOO_TJ_AVAX_USDC_LP",
        amount: amount
        }));
    }

    // ----- UNSTAKE -----

    /**
      * Unstakes PNG_AVAX_USDC_LP from the Beefy protocol
      * @dev This function uses the redstone-evm-connector
      * @param amount amount of PNG_AVAX_USDC_LP to be unstaked
    **/
    function unstakePngUsdcAvaxLpBeefy(uint256 amount) public onlyOwnerOrInsolvent nonReentrant recalculateAssetsExposure {
        _unstakeLpBeefy(IBeefyFinance.BeefyStakingDetails({
        lpTokenAddress: PNG_AVAX_USDC_LP,
        vaultAddress: MOO_PNG_AVAX_USDC_LP,
        lpTokenSymbol: "PNG_AVAX_USDC_LP",
        vaultTokenSymbol: "MOO_PNG_AVAX_USDC_LP",
        amount: amount
        }));
    }

    /**
      * Unstakes PNG_AVAX_USDCe_LP from the Beefy protocol
      * @dev This function uses the redstone-evm-connector
      * @param amount amount of PNG_AVAX_USDCe_LP to be unstaked
    **/
    function unstakePngUsdceAvaxLpBeefy(uint256 amount) public onlyOwnerOrInsolvent nonReentrant recalculateAssetsExposure {
        _unstakeLpBeefy(IBeefyFinance.BeefyStakingDetails({
        lpTokenAddress: PNG_AVAX_USDCe_LP,
        vaultAddress: MOO_PNG_AVAX_USDCe_LP,
        lpTokenSymbol: "PNG_AVAX_USDCe_LP",
        vaultTokenSymbol: "MOO_PNG_AVAX_USDCe_LP",
        amount: amount
        }));
    }

    /**
      * Untakes TJ_AVAX_USDC_LP from the Beefy protocol
      * @dev This function uses the redstone-evm-connector
      * @param amount amount of TJ_AVAX_USDC_LP to be unstaked
    **/
    function unstakeTjUsdcAvaxLpBeefy(uint256 amount) public onlyOwnerOrInsolvent nonReentrant recalculateAssetsExposure {
        _unstakeLpBeefy(IBeefyFinance.BeefyStakingDetails({
        lpTokenAddress: TJ_AVAX_USDC_LP,
        vaultAddress: MOO_TJ_AVAX_USDC_LP,
        lpTokenSymbol: "TJ_AVAX_USDC_LP",
        vaultTokenSymbol: "MOO_TJ_AVAX_USDC_LP",
        amount: amount
        }));
    }

    // ----- PRIVATE METHODS -----


    /**
      * Stakes {stakingDetails.lpTokenAddress} LP token in the Beefy protocol
      * @dev This function uses the redstone-evm-connector
      * @param stakingDetails IBeefyFinance.BeefyStakingDetails staking details
    **/
    function _stakeLpBeefy(IBeefyFinance.BeefyStakingDetails memory stakingDetails) private {
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();

        require(stakingDetails.amount > 0, "Cannot stake 0 tokens");
        // _ACTIVE = 2
        require(tokenManager.tokenToStatus(stakingDetails.lpTokenAddress) == 2, "LP token not supported");
        require(tokenManager.tokenToStatus(stakingDetails.vaultAddress) == 2, "Vault token not supported");
        require(IERC20(stakingDetails.lpTokenAddress).balanceOf(address(this)) >= stakingDetails.amount, "Not enough LP token available");

        stakingDetails.lpTokenAddress.safeApprove(stakingDetails.vaultAddress, 0);
        stakingDetails.lpTokenAddress.safeApprove(stakingDetails.vaultAddress, stakingDetails.amount);

        IBeefyFinance vaultContract = IBeefyFinance(stakingDetails.vaultAddress);

        vaultContract.deposit(stakingDetails.amount);

        // Add/remove owned tokens
        if(vaultContract.balanceOf(address(this)) > 0) {
            DiamondStorageLib.addOwnedAsset(stakingDetails.vaultTokenSymbol, stakingDetails.vaultAddress);
        }
        if(IERC20(stakingDetails.lpTokenAddress).balanceOf(address(this)) == 0) {
            DiamondStorageLib.removeOwnedAsset(stakingDetails.lpTokenSymbol);
        }

        emit Staked(msg.sender, stakingDetails.lpTokenSymbol, stakingDetails.vaultAddress, stakingDetails.amount, block.timestamp);
    }

    /**
              * Unstakes {stakingDetails.lpTokenAddress} LP token in the Beefy protocol
              * @dev This function uses the redstone-evm-connector
              * @param stakingDetails IBeefyFinance.BeefyStakingDetails staking details
    **/
    function _unstakeLpBeefy(IBeefyFinance.BeefyStakingDetails memory stakingDetails) private {
        IBeefyFinance vaultContract = IBeefyFinance(stakingDetails.vaultAddress);
        uint256 initialStakedBalance = vaultContract.balanceOf(address(this));

        require(initialStakedBalance >= stakingDetails.amount, "Cannot unstake more than was initially staked");

        vaultContract.withdraw(stakingDetails.amount);

        // Add/remove owned tokens
        if(IERC20(stakingDetails.lpTokenAddress).balanceOf(address(this)) > 0) {
            DiamondStorageLib.addOwnedAsset(stakingDetails.lpTokenSymbol, stakingDetails.lpTokenAddress);
        }
        if(vaultContract.balanceOf(address(this)) == 0) {
            DiamondStorageLib.removeOwnedAsset(stakingDetails.vaultTokenSymbol);
        }

        emit Unstaked(msg.sender, stakingDetails.lpTokenSymbol, stakingDetails.vaultAddress, stakingDetails.amount, block.timestamp);
    }


    /* ========== RECEIVE AVAX FUNCTION ========== */
    receive() external payable {}

    /**
        * @dev emitted when user stakes an asset
        * @param user the address executing staking
        * @param asset the asset that was staked
        * @param vault address of the vault token
        * @param amount of the asset that was staked
        * @param timestamp of staking
    **/
    event Staked(address indexed user, bytes32 indexed asset, address indexed vault, uint256 amount, uint256 timestamp);

    /**
        * @dev emitted when user unstakes an asset
        * @param user the address executing unstaking
        * @param asset the asset that was unstaked
        * @param vault address of the vault token
        * @param amount of the asset that was unstaked
        * @param timestamp of unstaking
    **/
    event Unstaked(address indexed user, bytes32 indexed asset, address indexed vault, uint256 amount, uint256 timestamp);
}

contracts/facets/avalanche/CurveFacet.so.content:
// SPDX-License-Identifier: BUSL-1.1
// Last deployed from commit: 799a1765b64edc5c158198ef84f785af79e234ae;
pragma solidity 0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

import "../../ReentrancyGuardKeccak.sol";
import "../../OnlyOwnerOrInsolvent.sol";
import "../../interfaces/ITokenManager.sol";
import "../../interfaces/ICurvePool.sol";
import {DiamondStorageLib} from "../../lib/DiamondStorageLib.sol";

//This path is updated during deployment
import "../../lib/avalanche/DeploymentConstants.sol";

contract CurveFacet is ReentrancyGuardKeccak, OnlyOwnerOrInsolvent {
    using TransferHelper for address;

    // Used to deposit/withdraw tokens
    address private constant CURVE_POOL_ADDRESS = 0x58e57cA18B7A47112b877E31929798Cd3D703b0f;
    // crvUSDBTCETH
    bytes32 private constant CURVE_TOKEN_SYMBOL = "crvUSDBTCETH";

    /**
     * Stakes tokens in Curve atricrypto pool
     * @param amounts amounts of tokens to be staked
     **/
    function stakeCurve(uint256[5] memory amounts) external nonReentrant onlyOwner recalculateAssetsExposure remainsSolvent {
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();

        address curveTokenAddress = DeploymentConstants.getTokenManager().getAssetAddress(CURVE_TOKEN_SYMBOL, false);
        IERC20 curveToken = IERC20(curveTokenAddress);
        uint256 initialCurveBalance = curveToken.balanceOf(address(this));

        bool allZero = true;
        uint256 numStakeTokens;
        for (uint256 i; i < 5; ++i ) {
            IERC20 token = IERC20(tokenManager.getAssetAddress(getTokenSymbol(i), false));
            amounts[i] = Math.min(token.balanceOf(address(this)), amounts[i]);
            if (amounts[i] > 0) {
                allZero = false;
                address(token).safeApprove(CURVE_POOL_ADDRESS, 0);
                address(token).safeApprove(CURVE_POOL_ADDRESS, amounts[i]);
                ++numStakeTokens;
            }
        }
        require(!allZero, "Cannot stake 0 tokens");

        bytes32[] memory stakedAssets = new bytes32[](numStakeTokens);
        uint256[] memory stakedAmounts = new uint256[](numStakeTokens);
        uint256 idx;
        for (uint256 i; i < 5; ++i ) {
            if (amounts[i] > 0) {
                stakedAssets[idx] = getTokenSymbol(i);
                stakedAmounts[idx++] = amounts[i];
            }
        }

        ICurvePool(CURVE_POOL_ADDRESS).add_liquidity(amounts, 0);

        // Add/remove owned tokens
        DiamondStorageLib.addOwnedAsset(CURVE_TOKEN_SYMBOL, curveTokenAddress);
        for (uint256 i; i < 5; ++i ) {
            IERC20 token = IERC20(tokenManager.getAssetAddress(getTokenSymbol(i), false));
            if (amounts[i] > 0 && token.balanceOf(address(this)) == 0) {
                DiamondStorageLib.removeOwnedAsset(getTokenSymbol(i));
            }
        }

        emit Staked(
            msg.sender,
            stakedAssets,
            curveTokenAddress,
            stakedAmounts,
            curveToken.balanceOf(address(this)) - initialCurveBalance,
            block.timestamp
        );
    }

    /**
     * Unstakes tokens from Curve atricrypto pool
     * @param amount amount of token to be unstaked
     **/
    function unstakeCurve(uint256 amount, uint256[5] memory min_amounts) external nonReentrant onlyOwnerOrInsolvent recalculateAssetsExposure {
        ICurvePool pool = ICurvePool(CURVE_POOL_ADDRESS);
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();
        address curveTokenAddress = tokenManager.getAssetAddress(CURVE_TOKEN_SYMBOL, true);
        IERC20 curveToken = IERC20(curveTokenAddress);
        uint256 curveTokenBalance = curveToken.balanceOf(address(this));
        uint256[5] memory initialDepositTokenBalances;
        for (uint256 i; i < 5; ++i) {
            IERC20 depositToken = IERC20(tokenManager.getAssetAddress(getTokenSymbol(i), true));
            initialDepositTokenBalances[i] = depositToken.balanceOf(address(this));
        }
        amount = Math.min(curveTokenBalance, amount);

        curveTokenAddress.safeApprove(CURVE_POOL_ADDRESS, 0);
        curveTokenAddress.safeApprove(CURVE_POOL_ADDRESS, amount);
        pool.remove_liquidity(amount, min_amounts);

        // Add/remove owned tokens
        uint256[5] memory unstakedAmounts;
        for (uint256 i; i < 5; ++i) {
            IERC20 depositToken = IERC20(tokenManager.getAssetAddress(getTokenSymbol(i), true));
            unstakedAmounts[i] = depositToken.balanceOf(address(this)) - initialDepositTokenBalances[i];
            DiamondStorageLib.addOwnedAsset(getTokenSymbol(i), address(depositToken));
        }
        uint256 newCurveTokenBalance = curveToken.balanceOf(address(this));
        if(newCurveTokenBalance == 0) {
            DiamondStorageLib.removeOwnedAsset(CURVE_TOKEN_SYMBOL);
        }

        emit Unstaked(
            msg.sender,
            getTokenSymbols(),
            curveTokenAddress,
            unstakedAmounts,
            curveTokenBalance - newCurveTokenBalance,
            block.timestamp
        );
    }

    // TODO: Add slippage control
    /**
     * Unstakes one token from Curve atricrypto pool
     * @param i index of token to be unstaked
     * @param amount amount of token to be unstaked
     **/
    function unstakeOneTokenCurve(uint256 i, uint256 amount) external nonReentrant onlyOwnerOrInsolvent recalculateAssetsExposure {
        require(i < 5, "Invalid token index");
        ICurvePool pool = ICurvePool(CURVE_POOL_ADDRESS);
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();
        address curveTokenAddress = tokenManager.getAssetAddress(CURVE_TOKEN_SYMBOL, true);
        IERC20 curveToken = IERC20(curveTokenAddress);
        IERC20 depositToken = IERC20(tokenManager.getAssetAddress(getTokenSymbol(i), true));
        uint256 initialDepositTokenBalance = depositToken.balanceOf(address(this));
        uint256 curveTokenBalance = curveToken.balanceOf(address(this));
        uint256 maxWithdrawAmount = pool.calc_withdraw_one_coin(curveTokenBalance, i);
        amount = Math.min(maxWithdrawAmount, amount);

        uint256 burnAmount = curveTokenBalance * amount / maxWithdrawAmount;
        curveTokenAddress.safeApprove(CURVE_POOL_ADDRESS, 0);
        curveTokenAddress.safeApprove(CURVE_POOL_ADDRESS, burnAmount);
        pool.remove_liquidity_one_coin(burnAmount, i, 0);

        // Add/remove owned tokens
        DiamondStorageLib.addOwnedAsset(getTokenSymbol(i), address(depositToken));
        uint256 newCurveTokenBalance = curveToken.balanceOf(address(this));
        if(newCurveTokenBalance == 0) {
            DiamondStorageLib.removeOwnedAsset(CURVE_TOKEN_SYMBOL);
        }

        emit UnstakedOneToken(
            msg.sender,
            getTokenSymbol(i),
            curveTokenAddress,
            depositToken.balanceOf(address(this)) - initialDepositTokenBalance,
            curveTokenBalance - newCurveTokenBalance,
            block.timestamp
        );
    }

    // INTERNAL FUNCTIONS

    function getTokenSymbols() internal pure returns (bytes32[5] memory tokenSymbols) {
        tokenSymbols = [
            bytes32("DAIe"),
            bytes32("USDCe"),
            bytes32("USDT.e"),
            bytes32("WBTCe"),
            bytes32("ETH")
        ];
    }

    function getTokenSymbol(uint256 i) internal pure returns (bytes32) {
        return getTokenSymbols()[i];
    }

    // MODIFIERS

    modifier onlyOwner() {
        DiamondStorageLib.enforceIsContractOwner();
        _;
    }

    // EVENTS

    /**
        * @dev emitted when user stakes assets
        * @param user the address executing staking
        * @param assets the assets that were staked
        * @param vault address of the vault token
        * @param depositTokenAmounts how much of deposit tokens was staked
        * @param receiptTokenAmount how much of receipt token was received
        * @param timestamp of staking
    **/
    event Staked(address indexed user, bytes32[] assets, address indexed vault, uint256[] depositTokenAmounts, uint256 receiptTokenAmount, uint256 timestamp);

    /**
        * @dev emitted when user unstakes assets
        * @param user the address executing staking
        * @param assets the assets that were unstaked
        * @param vault address of the vault token
        * @param depositTokenAmounts how much of deposit tokens was received
        * @param receiptTokenAmount how much of receipt token was unstaked
        * @param timestamp of unstaking
    **/
    event Unstaked(address indexed user, bytes32[5] assets, address indexed vault, uint256[5] depositTokenAmounts, uint256 receiptTokenAmount, uint256 timestamp);

    /**
        * @dev emitted when user unstakes an asset
        * @param user the address executing unstaking
        * @param vault address of the vault token
        * @param asset the asset that was unstaked
        * @param depositTokenAmount how much deposit token was received
        * @param receiptTokenAmount how much receipt token was unstaked
        * @param timestamp of unstaking
    **/
    event UnstakedOneToken(address indexed user, bytes32 indexed asset, address indexed vault, uint256 depositTokenAmount, uint256 receiptTokenAmount, uint256 timestamp);
}

contracts/facets/avalanche/GLPFacet.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// Last deployed from commit: 799a1765b64edc5c158198ef84f785af79e234ae;
pragma solidity 0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "../../interfaces/facets/avalanche/IGLPRewarder.sol";
import "../../interfaces/facets/avalanche/IRewardRouterV2.sol";
import "../../interfaces/facets/avalanche/IRewardTracker.sol";
import "../../ReentrancyGuardKeccak.sol";
import {DiamondStorageLib} from "../../lib/DiamondStorageLib.sol";
import "../../OnlyOwnerOrInsolvent.sol";
import "../../interfaces/ITokenManager.sol";

//This path is updated during deployment
import "../../lib/avalanche/DeploymentConstants.sol";

contract GLPFacet is ReentrancyGuardKeccak, OnlyOwnerOrInsolvent {
    using TransferHelper for address;

    // Used to claim GLP fees
    address private constant REWARD_ROUTER_ADDRESS = 0x82147C5A7E850eA4E28155DF107F2590fD4ba327;
    // Used to mint/redeem GLP
    address private constant GLP_REWARD_ROUTER_ADDRESS = 0xB70B91CE0771d3f4c81D87660f71Da31d48eB3B3;
    // Used to approve tokens to mint GLP with
    address private constant GLP_MANAGER_ADDRESS = 0xD152c7F25db7F4B95b7658323c5F33d176818EE4;
    // fsGLP
    address private constant GLP_TOKEN_ADDRESS = 0x9e295B5B976a184B14aD8cd72413aD846C299660;

    function claimGLpFees() external nonReentrant onlyOwner noBorrowInTheSameBlock recalculateAssetsExposure remainsSolvent {
        IRewardRouterV2 rewardRouter = IRewardRouterV2(REWARD_ROUTER_ADDRESS);
        IRewardTracker rewardTracker = IRewardTracker(rewardRouter.feeGlpTracker());

        require(rewardTracker.claimable(address(this)) > 0, "There are no claimable fees");

        IERC20Metadata wavaxToken = getERC20TokenInstance("AVAX", false);
        uint256 initialWavaxBalance = wavaxToken.balanceOf(address(this));

        rewardRouter.claimFees();

        uint256 postClaimingWavaxBalance = wavaxToken.balanceOf(address(this));

        // Add asset to ownedAssets
        if ((initialWavaxBalance == 0) && (postClaimingWavaxBalance > 0)) {
            DiamondStorageLib.addOwnedAsset("AVAX", address(wavaxToken));
        }

        emit GLPFeesClaim(msg.sender, postClaimingWavaxBalance-initialWavaxBalance, block.timestamp);
    }

    function mintAndStakeGlp(address _token, uint256 _amount, uint256 _minUsdg, uint256 _minGlp) external nonReentrant onlyOwner noBorrowInTheSameBlock recalculateAssetsExposure remainsSolvent{
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();
        require(tokenManager.isTokenAssetActive(GLP_TOKEN_ADDRESS), "GLP not supported.");
        require(tokenManager.isTokenAssetActive(_token), "Asset not supported.");

        require(_amount > 0, "Amount of GLP to mint  has to be greater than 0");

        IERC20Metadata tokenToMintWith = IERC20Metadata(_token);
        bytes32 tokenToMintWithSymbol = tokenManager.tokenAddressToSymbol(_token);
        IGLPRewarder glpRewarder = IGLPRewarder(GLP_REWARD_ROUTER_ADDRESS);
        IERC20Metadata glpToken = IERC20Metadata(GLP_TOKEN_ADDRESS);

        uint256 glpInitialBalance = glpToken.balanceOf(address(this));

        _amount = Math.min(tokenToMintWith.balanceOf(address(this)), _amount);

        _token.safeApprove(GLP_MANAGER_ADDRESS, 0);
        _token.safeApprove(GLP_MANAGER_ADDRESS, _amount);

        uint256 glpOutputAmount = glpRewarder.mintAndStakeGlp(_token, _amount, _minUsdg, _minGlp);

        require((glpToken.balanceOf(address(this)) - glpInitialBalance) == glpOutputAmount, "GLP minted and balance difference mismatch");
        require(glpOutputAmount >=_minGlp, "Insufficient output amount");

        // Add asset to ownedAssets
        if (glpToken.balanceOf(address(this)) > 0) {
            DiamondStorageLib.addOwnedAsset("GLP", GLP_TOKEN_ADDRESS);
        }

        // Remove asset from ownedAssets if the asset balance is 0 after the mint
        if (tokenToMintWith.balanceOf(address(this)) == 0) {
            DiamondStorageLib.removeOwnedAsset(tokenToMintWithSymbol);
        }

        emit GLPMint(
            msg.sender,
            tokenToMintWithSymbol,
            _amount,
            glpOutputAmount,
            block.timestamp
        );

    }

    function unstakeAndRedeemGlp(address _tokenOut, uint256 _glpAmount, uint256 _minOut) external nonReentrant onlyOwnerOrInsolvent noBorrowInTheSameBlock recalculateAssetsExposure    {
        ITokenManager tokenManager = DeploymentConstants.getTokenManager();
        require(tokenManager.isTokenAssetActive(_tokenOut), "Asset not supported.");

        require(_glpAmount > 0, "Amount of GLP to redeem has to be greater than 0");

        IERC20Metadata redeemedToken = IERC20Metadata(_tokenOut);
        bytes32 redeemedTokenSymbol = tokenManager.tokenAddressToSymbol(_tokenOut);
        IGLPRewarder glpRewarder = IGLPRewarder(GLP_REWARD_ROUTER_ADDRESS);
        IERC20Metadata glpToken = IERC20Metadata(GLP_TOKEN_ADDRESS);

        uint256 redeemedTokenInitialBalance = redeemedToken.balanceOf(address(this));
        _glpAmount = Math.min(glpToken.balanceOf(address(this)), _glpAmount);

        uint256 redeemedAmount = glpRewarder.unstakeAndRedeemGlp(_tokenOut, _glpAmount, _minOut, address(this));

        require((redeemedToken.balanceOf(address(this)) - redeemedTokenInitialBalance) == redeemedAmount, "Redeemed token amount and balance difference mismatch");
        require(redeemedAmount >= _minOut, "Insufficient output amount");

        // Add asset to ownedAssets
        if (redeemedToken.balanceOf(address(this)) > 0) {
            DiamondStorageLib.addOwnedAsset(redeemedTokenSymbol, _tokenOut);
        }

        // Remove asset from ownedAssets if the asset balance is 0 after the redemption
        if (glpToken.balanceOf(address(this)) == 0) {
            DiamondStorageLib.removeOwnedAsset("GLP");
        }

        emit GLPRedemption(
            msg.sender,
            redeemedTokenSymbol,
            _glpAmount,
            redeemedAmount,
            block.timestamp
        );

    }

    modifier onlyOwner() {
        DiamondStorageLib.enforceIsContractOwner();
        _;
    }

    /**
     * @dev emitted after a GLP token mint
     * @param user the address of user minting GLP
     * @param tokenToMintWith token which GLP was minted with
     * @param tokenToMintWithAmount amount of token used to mint GLP
     * @param glpOutputAmount amount of GLP minted
     * @param timestamp time of the mint
     **/
    event GLPMint(address indexed user, bytes32 indexed tokenToMintWith, uint256 tokenToMintWithAmount, uint256 glpOutputAmount, uint256 timestamp);

    /**
  * @dev emitted after a GLP token redemption
  * @param user the address of user redeeming GLP
  * @param redeemedToken token which GLP was redeemed into
  * @param glpRedeemedAmount amount of GLP redeemed
  * @param redeemedTokenAmount amount of redeemedToken redeemed
  * @param timestamp time of the redemption
  **/
    event GLPRedemption(address indexed user, bytes32 indexed redeemedToken, uint256 glpRedeemedAmount, uint256 redeemedTokenAmount, uint256 timestamp);

    /**
    * @dev emitted after claiming GLP fees
    * @param user the address of user claiming fees
    * @param wavaxAmountClaimed amount of wavax fees that were claimed
    * @param timestamp time of claiming the fees
    **/
    event GLPFeesClaim(address indexed user, uint256 wavaxAmountClaimed, uint256 timestamp);
}

